mapper.select:
select distinct a.ormtno,a.SPTYNO,a.SPSENO,b.VERSNO,a.PLSPNO,a.PLSPNM,b.SAMPNO,b.SAMPNM,c.suitno,d.ORMTQT ,d.mtorno ,d.mlorno ,NVL(X.packqt,1) packqt ,f.SIZETY,f.SIZENO,f.ORSZQT,f.ORBGQT,nvl(f.ORSZST,0) ORSZST,z.PRODNM from ORD_SAMPLE_PLAN a inner join ORD_SAMPLE_DESIGN b on a.plspno=b.plspno inner join ORD_SAMPLE_DESIGN_SIZEGP c on b.sampno=c.sampno left join ORD_ORDDTL d on c.sampno=d.sampno and c.suitno=d.suitno and d.mtorno=#{mtorno} and d.ORMTQT!=0 left join ORD_ORDHD e on d.mtorno=e.mtorno and d.mlorno=e.mlorno left join ORD_ORDSZDTL f on d.mtorno=f.mtorno and d.mlorno=e.mlorno and d.sampno=f.sampno and d.suitno=f.suitno left join ord_sample_prod z on b.sampno=z.sampno and a.ormtno=z.ormtno and c.suitno=z.suitno LEFT JOIN (SELECT FSZTY,FSZNO,SUM(SIZEQT) packqt FROM ORD_PUB_SIZEDTL WHERE FSZTY='PRDPK' AND SIZETY='STDSZ' GROUP BY FSZTY,FSZNO) X ON F.SIZETY=X.FSZTY AND F.SIZENO=X.FSZNO where c.suitno=#{suitno} and a.ormtno=#{ormtno} AND A.BRADNO=#{bradno} and a.spclno=#{spclno} and c.sizegp=#{sizegp}
and a.sptyno=#{sptyno}
and a.spseno=#{spseno}
and b.versno=#{versno}
and lower(b.sampnm) like lower('%${sampnm}%')
and e.szstat =#{szstat}
order by a.SPTYNO,a.SPSENO,b.VERSNO,a.PLSPNO,a.PLSPNM,b.SAMPNO,b.SAMPNM,c.suitno

     	SELECT max(szstat) FROM ORD_ORD a,ORD_ORDhd b
		where a.mtorno=b.mtorno and a.ormtno=#{ormtno}
		and a.ortyno=#{ortyno} and a.ordorg=#{ordorg} and channo=#{channo}
		and b.bradno=#{bradno} and b.spclno=#{spclno}

mapper.update:
update ord_orddtl a set ormtqt=(select sum (orszqt) orszqt from ORD_ORDSZDTL b where a.mtorno=b.mtorno and a.sampno=b.sampno and a.suitno=b.suitno), ormtqs=ormtqt
, ormtqt1=ormtqt
where a.mtorno=#{mtorno} and a.sampno=#{sampno} and a.suitno=#{suitno}

    	update ORD_ORDSZDTL set ORSZST=1 
		where MTORNO=#{mtorno}
		and MLORNO in (select MLORNO from ORD_ORDHD a where a.mtorno=#{mtorno} and a.bradno=#{bradno})
