mapper.select:
select x.*,y.YXGSNO,y.ORMTQT_TP_YXGS,y.ORMTQT_DZ_YXGS,'${bradno}' bradno ,'${spclno}' spclno,'${ormtno}' ormtno from ( select B.SAMPNO,c.SAMPNM,B.SUITNO,d.SPTYNO,d.SPSENO,c.PACKQT,sum(b.ORMTQT) ORMTQT_TP_GSBB from ORD_ORD A inner join ORD_ORDhd e on A.mtorno=e.mtorno and e.orstat='3' inner join ORD_ORDDTL B on A.mtorno=B.mtorno and e.mlorno=b.mlorno inner join ord_sample_design c on b.sampno=c.sampno inner join ord_sample_plan d on c.plspno=d.plspno WHERE A.ORMTNO=#{ormtno} and d.ormtno=#{ormtno} and A.ORTYNO='TP' and a.channo='GSBB'
and d.bradno=#{bradno}
and d.spclno=#{spclno}
and d.sptyno=#{sptyno}
and d.spseno=#{spseno}
and lower(c.sampnm) like lower('%${sampnm}%')
GROUP BY B.SAMPNO,c.sampnm,B.SUITNO,d.sptyno,d.spseno,c.PACKQT ) x left join ( select SAMPNO,YXGSNO,SUITNO,sum(ORMTQT_TP_YXGS) ORMTQT_TP_YXGS,sum(ORMTQT_DZ_YXGS) ORMTQT_DZ_YXGS from ( select B.SAMPNO,a.ordorg as YXGSNO,B.SUITNO,sum(b.ORMTQT) ORMTQT_TP_YXGS,0 as ORMTQT_DZ_YXGS from ORD_ORD A inner join ORD_ORDDTL B on A.mtorno=B.mtorno WHERE A.ORMTNO=#{ormtno} and A.ORTYNO='TP' and a.channo='YXGS' GROUP BY B.SAMPNO,a.ordorg,B.SUITNO union all select B.SAMPNO,C.YXGSNO,B.SUITNO,0 as ORMTQT_TP_YXGS,sum(b.ORMTQS) ORMTQT_DZ_YXGS from ORD_ORD A inner join ORD_ORDDTL B on A.mtorno=B.mtorno inner join (SELECT DISTINCT YXGSNO,QYNO FROM V_ORD_ORG) C on A.ORDORG=C.QYNO WHERE A.ORMTNO=#{ormtno} and A.ORTYNO='DZ' and a.channo='QY' GROUP BY B.SAMPNO,C.YXGSNO,B.SUITNO ) GROUP BY SAMPNO,YXGSNO,SUITNO ) y on x.SAMPNO=y.SAMPNO and x.suitno=y.suitno order by x.SAMPNO,x.suitno,y.YXGSNO

    	select distinct z.sampnm from (
	     	select  c.sampno,c.suitno,sum(c.ormtqt) ormtqt from ORD_ORDhd b,ORD_ORDdtl c where b.mtorno=c.mtorno and  b.mlorno=c.mlorno and b.mtorno=#{mtorno}
		    and b.bradno=#{bradno} and b.spclno=#{spclno}
		    group by c.sampno,c.suitno
	    ) x left join (
		    select c.sampno,c.suitno,sum(c.ormtqt) ormtqt from ORD_ORD a,ORD_ORDhd b,ORD_ORDdtl c
		    where a.mtorno=b.mtorno and a.mtorno=c.mtorno and a.channo='YXGS' and a.ORTYNO='TP' and b.orstat=0 and a.ormtno=#{ormtno}
		    and b.bradno=#{bradno} and b.spclno=#{spclno} 
		    group by c.sampno,c.suitno
	    ) y on x.sampno=y.sampno and x.suitno=y.suitno
	    inner join ord_sample_design z on x.sampno=z.sampno 
	    where  x.ormtqt!=y.ormtqt  or y.ormtqt is null

    select distinct b.sampnm,c.YXGSNM from ORD_ORDdtl a ,ord_sample_design b
       , (
		      select distinct b.mlorno,c.YXGSNM from ORD_ORD a,ORD_ORDhd b,(SELECT DISTINCT YXGSNO,YXGSNM FROM  V_ORD_ORG) C 
		      where a.mtorno=b.mtorno and A.ORDORG=C.YXGSNO and a.channo='YXGS' and a.ortyno='TP' and b.isfect=0 and b.orstat=0 and a.ormtno=#{ormtno}
		       and b.bradno=#{bradno} and b.spclno=#{spclno} 
    ) c where a.mlorno =c.mlorno and a.sampno=b.sampno and trunc(a.ormtqt/b.packqt)!=(a.ormtqt)/b.packqt
    order by b.sampnm,c.YXGSNM

mapper.update:
    	update ORD_ORDhd set isfect=1,orstat=3
		where mlorno in (
		      select b.mlorno from ORD_ORD a,ORD_ORDhd b
		      where a.mtorno=b.mtorno and a.channo='YXGS' and a.ortyno='TP' and b.isfect=0 and b.orstat=0 and a.ormtno=#{ormtno}
		) and bradno=#{bradno} and spclno=#{spclno}

mapper.select:
    	select A.* from t_org A,T_ORG_ORG B 
		where A.ORGNO=B.CHILD_NO AND orgty='REGN' AND ORGST='KQ' AND B.DIM='SALE' AND B.PARENT_NO=#{yxgsno} 
		ORDER BY ORGSO

select x.*,y.QYNO,y.ORMTQT_TP_QY,y.ORMTQT_TP_TX,y.ORMTQT_DZ_QY,y.ORMTQT_DZ_TX ,'${bradno}' bradno ,'${spclno}' spclno,'${ormtno}' ormtno from ( select B.SAMPNO,c.sampnm,B.SUITNO,d.sptyno,d.spseno,c.PACKQT,sum(b.ORMTQT) ORMTQT_TP_YXGS from ORD_ORD A inner join ORD_ORDhd e on A.mtorno=e.mtorno and e.orstat='3' inner join ORD_ORDDTL B on A.mtorno=B.mtorno and e.mlorno=b.mlorno inner join ord_sample_design c on b.sampno=c.sampno inner join ord_sample_plan d on c.plspno=d.plspno WHERE A.ORMTNO=#{ormtno} and d.ormtno=#{ormtno} and A.ORTYNO='TP' and a.channo='YXGS' AND A.ORDORG=#{yxgsno}
and d.bradno=#{bradno}
and d.spclno=#{spclno}
and d.sptyno=#{sptyno}
and d.spseno=#{spseno}
and lower(c.sampnm) like lower('%${sampnm}%')
GROUP BY B.SAMPNO,c.sampnm,B.SUITNO,d.sptyno,d.spseno,c.PACKQT ) x left join ( select SAMPNO,QYNO,SUITNO,sum(ORMTQT_TP_QY) ORMTQT_TP_QY,sum(ORMTQT_TP_TX) ORMTQT_TP_TX,sum(ORMTQT_DZ_QY) ORMTQT_DZ_QY,sum(ORMTQT_DZ_TX) ORMTQT_DZ_TX from ( select B.SAMPNO,a.ordorg as QYNO,B.SUITNO,(sum(b.ORMTQT)-sum(b.ORMTQT1)) ORMTQT_TP_QY,sum(b.ORMTQT1) ORMTQT_TP_TX,0 as ORMTQT_DZ_QY,0 as ORMTQT_DZ_TX from ORD_ORD A inner join ORD_ORDDTL B on A.mtorno=B.mtorno inner join (SELECT DISTINCT QYNO FROM V_ORD_ORG WHERE YXGSNO=#{yxgsno}) C on A.ORDORG=C.QYNO WHERE A.ORMTNO=#{ormtno} and A.ORTYNO='TP' and a.channo='QY' GROUP BY B.SAMPNO,a.ordorg,B.SUITNO union all select B.SAMPNO,C.QYNO,B.SUITNO,0 as ORMTQT_TP_QY,0 AS ORMTQT_TP_TX,sum(DECODE(a.channo,'QY', b.ORMTQS,0)) ORMTQT_DZ_QY,sum(DECODE(a.channo,'TX', b.ORMTQS,0)) as ORMTQT_DZ_TX from ORD_ORD A inner join ORD_ORDDTL B on A.mtorno=B.mtorno inner join (SELECT DISTINCT QYNO FROM V_ORD_ORG WHERE YXGSNO=#{yxgsno}) C on A.ORDORG=C.QYNO WHERE A.ORMTNO=#{ormtno} and A.ORTYNO='DZ' and a.channo IN ('QY','TX') GROUP BY B.SAMPNO,C.QYNO,B.SUITNO ) GROUP BY SAMPNO,QYNO,SUITNO ) y on x.SAMPNO=y.SAMPNO and x.suitno=y.suitno order by x.SAMPNO,x.suitno,y.QYNO

    	select distinct z.sampnm from (
		    select  c.sampno,c.suitno,sum(c.ormtqt) ormtqt from ORD_ORDhd b,ORD_ORDdtl c where b.mtorno=c.mtorno and  b.mlorno=c.mlorno and b.mtorno=#{mtorno}
		    and b.bradno=#{bradno} and b.spclno=#{spclno}
		    group by c.sampno,c.suitno
	    ) x left join (
		    select c.sampno,c.suitno,sum(c.ormtqt) ormtqt from ORD_ORD a,ORD_ORDhd b,ORD_ORDdtl c
		    where a.mtorno=b.mtorno and a.mtorno=c.mtorno and a.channo='QY' and a.ORTYNO='TP'  and b.orstat=0 and a.ormtno=#{ormtno}
		    AND A.ORDORG IN (SELECT DISTINCT QYNO FROM V_ORD_ORG WHERE YXGSNO=#{yxgsno})
		    and b.bradno=#{bradno} and b.spclno=#{spclno} 
		    group by c.sampno,c.suitno
	    ) y on x.sampno=y.sampno and x.suitno=y.suitno
	    inner join ord_sample_design z on  x.sampno=z.sampno 
	    where  x.ormtqt!=y.ormtqt or y.ormtqt is null

    select distinct b.sampnm,c.qynm from ORD_ORDdtl a ,ord_sample_design b
       , (
		      select distinct b.mlorno,c.qynm from ORD_ORD a,ORD_ORDhd b,(SELECT DISTINCT QYNO,qynm FROM  V_ORD_ORG WHERE YXGSNO=#{yxgsno}) C 
		      where a.mtorno=b.mtorno and A.ORDORG=C.QYNO and a.channo='QY' and a.ortyno='TP' and b.isfect=0 and b.orstat=0 and a.ormtno=#{ormtno}
		       and b.bradno=#{bradno} and b.spclno=#{spclno} 
    ) c where a.mlorno =c.mlorno and a.sampno=b.sampno and trunc(a.ormtqt/b.packqt)!=(a.ormtqt)/b.packqt
    order by b.sampnm,c.qynm

mapper.update:
    	update ORD_ORDhd set isfect=1,orstat=3
		where mlorno in (
		      select distinct b.mlorno from ORD_ORD a,ORD_ORDhd b,(SELECT DISTINCT QYNO FROM  V_ORD_ORG WHERE YXGSNO=#{yxgsno}) C 
		      where a.mtorno=b.mtorno and A.ORDORG=C.QYNO and a.channo='QY' and a.ortyno='TP' and b.isfect=0 and b.orstat=0 and a.ormtno=#{ormtno}
		)
		and bradno=#{bradno} and spclno=#{spclno}
