mapper.select:
     	 SELECT distinct a.sizety,a.sizeno,d.sizenm FROM 
	    ord_ordszdtl a,ord_sample_design b,ord_sample_design_sizegp c,ORD_PUB_SIZE d
	    where a.sampno=b.sampno and b.sampno=c.sampno and a.suitno=c.suitno and a.sizeno=d.sizeno
	    and a.sizety=d.sizety --and a.sizety='STDSZ' 

	    	 and c.sizegp=#{sizegp}


	    and a.suitno=#{suitno}
	    and a.mlorno=#{mlorno}
	    and a.orbgqt!=0
	    order by a.sizeno

	    select x.sampno,x.sampnm,x.suitno,x.sptyno,x.spseno,x.versno,x.sizety,x.sizeno,(x.orbgqt-nvl(z.ORSZQT,0)) orbgqt
	    	,y.ORMMNO,y.MMORNO,y.ORSZQT,y.mldate,y.pldate,y.pplace,y.ordorg
	    	,u.prodnm,NVL(v.packqt,1) packqt	
	    	 from (
	     	 SELECT  b.sampno,b.sampnm,c.suitno,d.sptyno,d.spseno,b.versno,a.sizety,a.sizeno,a.orbgqt FROM 
		    ord_ordszdtl a,ord_sample_design b,ord_sample_design_sizegp c,ord_sample_plan d
		    where a.sampno=b.sampno and b.sampno=c.sampno and a.suitno=c.suitno and b.plspno=d.plspno
		    --and a.sizety='STDSZ' 

		    	 and c.sizegp=#{sizegp}


		     and a.suitno=#{suitno} 
		    and a.mlorno=#{mlorno} 
		    and a.orbgqt!=0

				and d.sptyno=#{sptyno}


				and d.spseno=#{spseno}


				and b.versno=#{versno}


				and lower(b.sampnm) like lower('%${sampnm}%')


		) x 
		left join (  
			select b.ORMMNO,w.mldate,w.pldate,w.pplace,c.* from ORD_BW_ORDMT a 
			inner join ORD_BW_ORDHD b on a.ORMMNO=b.ORMMNO
			inner join ORD_BW_ORDSZDTL c on b.MMORNO=c.MMORNO
			left join ord_bw_orddt w on b.ORMMNO=w.ORMMNO and b.mmorno=w.mmorno and c.ordorg=w.ordorg and c.sampno=w.sampno and c.suitno=w.suitno
		    where   
		    a.ormtno=#{ormtno} and c.ordorg=#{ordorg} and b.bradno=#{bradno} and b.spclno=#{spclno}
		    and b.orstat=0
		) y on x.sampno=y.sampno and x.suitno=y.suitno  and x.sizety=y.sizety and x.sizeno=y.sizeno
		left join (  
			select c.sampno,c.suitno,c.sizety,c.sizeno,sum(c.ORSZQT) ORSZQT from ORD_BW_ORDMT a,ORD_BW_ORDHD b,ORD_BW_ORDSZDTL c
		    where a.ORMMNO=b.ORMMNO and b.MMORNO=c.MMORNO
		    and a.ormtno=#{ormtno} and c.ordorg=#{ordorg} and b.bradno=#{bradno} and b.spclno=#{spclno}
		    and b.orstat=1
		    group by c.sampno,c.suitno,c.sizety,c.sizeno
		) z on x.sampno=z.sampno and x.suitno=z.suitno  and x.sizety=z.sizety and x.sizeno=z.sizeno
		left join ORD_SAMPLE_PROD u on x.sampno=u.sampno and x.suitno=u.suitno and u.ormtno=#{ormtno}
		LEFT JOIN (         SELECT FSZTY,FSZNO,SUM(SIZEQT) packqt FROM ORD_PUB_SIZEDTL WHERE FSZTY='PRDPK' AND SIZETY='STDSZ' 
         GROUP BY FSZTY,FSZNO) v ON x.SIZETY=v.FSZTY AND x.SIZENO=v.FSZNO 

		where 1=1

			and lower(u.prodnm) like lower('%${prodnm}%')

		order by x.SPTYNO,x.SPSENO,x.VERSNO,x.SAMPNO,x.SAMPNM,x.suitno
