mapper.select:
select a.*,b.orgnm,b.yxgsnm,c.channm,d.loginname,d.pwd from ORD_ORG a,v_ord_org b,t_org_chancl c,t_user d
where a.ordorg=b.orgno and a.channo=c.channo and lower(a.ordorg)=lower(d.loginname)  and a.ormtno=#{ormtno} and b.yxgsno=#{yxgsno}


	 and a.channo=#{channo}

    	select count(*)
		from ORD_ORG a,v_ord_org b
		where a.ordorg=b.orgno and a.ormtno=#{ormtno} and b.yxgsno=#{yxgsno}

    		 and a.channo=#{channo}

    	select *
		from ORD_ORG a
		where a.ordorg=#{orgno} and a.ormtno=#{ormtno}

select a.*,b.orgnm,d.loginname,d.pwd from ORD_ORG a,t_org b,t_user d
where a.ordorg=b.orgno and lower(a.ordorg)=lower(d.loginname)  and a.ormtno=#{ormtno}
and b.orgno in (${in_param})
