mapper.select:
select a.* from t_user a,t_position_org_user b
where a.id=b.user_id and b.position_id=#{position_id}

	and a.loginName like #{loginName}


	and a.name like #{name}

select count(a.id) from t_user a,t_position_org_user b
where a.id=b.user_id and b.position_id=#{position_id}

	and a.loginName like #{loginName}


	and a.name like #{name}
