mapper.select:
SELECT P0.ITNM AS SPCLNM,P2.ITNM AS SPTYNM,P5.ITNM AS SPSENM,P10.ITNM AS SPRSENM,P8.ITNM AS SPSEANM,P23.ITNM AS SPBANM, A.SAMPNM,H.PRODNM,A.GUSTNO,P9.ITNM AS COLRNM,DECODE(M.MTTYPE,'gc','国产','进口')MTTYPE,M.MTCOMP,M.YARMCT,M.GRAMWT,A.DESP, C.SPCTPR,'',B.SPFTPR,B.SPRTPR,NVL(CT.PLDTCT,1)PLDTCT,NVL(DT.PLDATE,B.PLDATE)PLDATE,S.IDSUNM,P29.ITNM AS SPMTNM,O.ORMTQT FROM ORD_SAMPLE_DESIGN A INNER JOIN ORD_SAMPLE_PLAN B ON A.PLSPNO=B.PLSPNO INNER JOIN(SELECT A.ORMTNO,C.SAMPNO,SUM(ORMTQT)ORMTQT FROM ORD_ORD A INNER JOIN ORD_ORDDTL C ON A.MTORNO=C.MTORNO INNER JOIN ORD_ORDHD B ON A.MTORNO=B.MTORNO AND B.MLORNO=C.MLORNO AND B.MLORVN=C.MLORVN WHERE A.ORMTNO=#{ormtno} AND B.BRADNO=#{bradno}
AND B.ISFECT=1 AND C.SUITNO IN('T00','T01') GROUP BY A.ORMTNO,C.SAMPNO having SUM(ORMTQT)!=0) O ON B.ORMTNO=O.ORMTNO AND A.SAMPNO=O.SAMPNO INNER JOIN ORD_SAMPLE_COLTH C ON A.SAMPNO=C.SAMPNO LEFT JOIN ORD_SAMPLE_MATE M ON A.SAMPNO=M.SAMPNO AND M.MATESO=1 LEFT JOIN ORD_SAMPLE_DESIGN_STPR P ON A.SAMPNO=P.SAMPNO AND P.SUITNO='T00' LEFT JOIN (SELECT ORMTNO,SAMPNO,MAX(PRODNM)PRODNM FROM ORD_SAMPLE_PROD WHERE ORMTNO=#{ormtno} GROUP BY ORMTNO,SAMPNO)H ON B.ORMTNO=H.ORMTNO AND A.SAMPNO=H.SAMPNO INNER JOIN ORD_PUB_CODE P0 ON P0.TYNO='0' AND B.SPCLNO=P0.ITNO INNER JOIN ORD_PUB_CODE P2 ON P2.TYNO='2' AND B.SPCLNO=P2.FITNO AND B.SPTYNO=P2.ITNO INNER JOIN ORD_PUB_CODE P5 ON P5.TYNO='5' AND B.SPCLNO=P5.FITNO AND B.SPSENO=P5.ITNO INNER JOIN ORD_PUB_CODE P10 ON P10.TYNO='10' AND B.SPRSENO=P10.ITNO INNER JOIN ORD_PUB_CODE P8 ON P8.TYNO='8' AND B.SPSEAN=P8.ITNO INNER JOIN ORD_PUB_CODE P23 ON P23.TYNO='23' AND B.SPBANO=P23.ITNO INNER JOIN ORD_PUB_CODE P9 ON P9.TYNO='9' AND A.COLRNO=P9.ITNO INNER JOIN ORD_PUB_CODE P29 ON P29.TYNO='29' AND A.SPMTNO=P29.ITNO LEFT JOIN ORD_PUB_SUNO S ON S.IDSUNO=NVL(C.PRSUNO,C.SPSUNO) LEFT JOIN(SELECT ORMTNO,SAMPNO,MIN(PLDATE)PLDATE FROM ORD_ORD_DWDT WHERE ORMTNO=#{ormtno} GROUP BY ORMTNO,SAMPNO) DT ON B.ORMTNO=DT.ORMTNO AND A.SAMPNO=DT.SAMPNO LEFT JOIN(SELECT ORMTNO,SAMPNO,COUNT(DISTINCT PLDATE) PLDTCT FROM ORD_ORD_DWDT WHERE ORMTNO=#{ormtno} GROUP BY ORMTNO,SAMPNO)CT ON B.ORMTNO=CT.ORMTNO AND A.SAMPNO=CT.SAMPNO WHERE B.ORMTNO=#{ormtno} AND B.BRADNO=#{bradno} AND B.SPCLNO=#{spclno}
and b.sptyno=#{sptyno}
and b.spseno=#{spseno}
and lower(a.sampnm) like lower('%${sampnm}%')
and lower(h.prodnm) like lower('%${prodnm}%')
and a.spmtno =#{spmtno}
and lower(s.idsunm) like lower('%${idsunm}%')

SELECT A.SAMPNM,H.PRODNM,S1.IDSUNM,S2.IDSUNM AS NWSUNM,NVL(T.MLTYPE,DECODE(M.MTTYPE,'gc','国产','进口'))MTTYPE,M.MATENO,T.MLITNO,M.MATESO,NVL(DT.PLDATE,B.PLDATE)PLDATE,NVL(DT.MLDATE,B.MLDATE)MLDATE, M.MTPUPR,T.HTTRPR,NVL(T.MLCOMP,M.MTCOMP)MTCOMP,NVL(T.MLSZZS,M.YARMCT)YARMCT,NVL(T.MLKZMD,M.GRAMWT)GRAMWT,NVL(T.MLWDTH,M.WIDTH)MLWDTH,O.ORMTQT,M.MTCNQT,O.ORMTQT*M.MTCNQT AS ORMLQT,T.HTTRQT,T.HTORDT, P8.ITNM AS SPSEANM,P23.ITNM AS SPBANM,P9.ITNM AS COLRNM,P13.ITNM AS VERSNM,P17.ITNM AS SPBSENM,P0.ITNM AS SPCLNM,P2.ITNM AS SPTYNM,P5.ITNM AS SPSENM FROM ORD_SAMPLE_DESIGN A INNER JOIN ORD_SAMPLE_PLAN B ON A.PLSPNO=B.PLSPNO INNER JOIN(SELECT A.ORMTNO,C.SAMPNO,SUM(ORMTQT)ORMTQT FROM ORD_ORD A INNER JOIN ORD_ORDDTL C ON A.MTORNO=C.MTORNO INNER JOIN ORD_ORDHD B ON A.MTORNO=B.MTORNO AND B.MLORNO=C.MLORNO AND B.MLORVN=C.MLORVN WHERE A.ORMTNO=#{ormtno} AND B.BRADNO=#{bradno} --订单有效状态（固定参数 1），套件（固定参数） AND B.ISFECT=1 AND C.SUITNO IN('T00','T01') GROUP BY A.ORMTNO,C.SAMPNO having SUM(ORMTQT)!=0) O ON B.ORMTNO=O.ORMTNO AND A.SAMPNO=O.SAMPNO LEFT JOIN ORD_SAMPLE_MATE M ON A.SAMPNO=M.SAMPNO LEFT JOIN (SELECT ORMTNO,SAMPNO,MAX(PRODNM)PRODNM FROM ORD_SAMPLE_PROD WHERE ORMTNO=#{ormtno} GROUP BY ORMTNO,SAMPNO)H ON B.ORMTNO=H.ORMTNO AND A.SAMPNO=H.SAMPNO INNER JOIN ORD_PUB_CODE P0 ON P0.TYNO='0' AND B.SPCLNO=P0.ITNO INNER JOIN ORD_PUB_CODE P2 ON P2.TYNO='2' AND B.SPCLNO=P2.FITNO AND B.SPTYNO=P2.ITNO INNER JOIN ORD_PUB_CODE P5 ON P5.TYNO='5' AND B.SPCLNO=P5.FITNO AND B.SPSENO=P5.ITNO INNER JOIN ORD_PUB_CODE P13 ON P13.TYNO='13' AND A.VERSNO=P13.ITNO INNER JOIN ORD_PUB_CODE P17 ON P17.TYNO='17' AND B.SPBSENO=P17.ITNO INNER JOIN ORD_PUB_CODE P8 ON P8.TYNO='8' AND B.SPSEAN=P8.ITNO INNER JOIN ORD_PUB_CODE P23 ON P23.TYNO='23' AND B.SPBANO=P23.ITNO INNER JOIN ORD_PUB_CODE P9 ON P9.TYNO='9' AND A.COLRNO=P9.ITNO LEFT JOIN ORD_PUB_SUNO S1 ON S1.IDSUNO=M.MTSUNO LEFT JOIN(SELECT ORMTNO,SAMPNO,MIN(MLDATE)MLDATE,MIN(PLDATE)PLDATE FROM ORD_ORD_DWDT WHERE ORMTNO=#{ormtno} GROUP BY ORMTNO,SAMPNO) DT ON B.ORMTNO=DT.ORMTNO AND A.SAMPNO=DT.SAMPNO LEFT JOIN ORD_MATE_POHED T ON B.ORMTNO=T.MLORMT AND A.SAMPNO=T.MLSMNO AND M.MATESO=T.MLZLPL LEFT JOIN ORD_PUB_SUNO S2 ON T.MLSUNO=S2.IDSUNO WHERE B.ORMTNO=#{ormtno} AND B.BRADNO=#{bradno} AND B.SPCLNO=#{spclno}
and b.sptyno=#{sptyno}
and b.spseno=#{spseno}
and lower(a.sampnm) like lower('%${sampnm}%')
and h.prodnm like '%${prodnm}%'
and s.idsunm like '%${idsunm}%'
and a.spmtno =#{spmtno}
order by a.sampnm,m.mateso

    	SELECT HTSEQN,HTTRQT,HTTRPR,HTORDT,HTMARK FROM ORD_MATE_PODTL 
		WHERE HTORMT=#{ormtno} AND HTITNO=#{htitno}
		ORDER BY HTSEQN

	 select b.sampnm,a.bradno,a.spclno,a.sptyno,a.spseno,a.spbano,a.sprtpr,c.* 
          from ORD_SAMPLE_PLAN a
      inner join ORD_SAMPLE_DESIGN b on A.PLSPNO=B.PLSPNO 
      inner join (
        select a.CHANNO,org.yxgsno ,org.yxgsnm,c.SAMPNO,sum(c.ORMTQT) ORMTQT
        from ORD_ORD a,ord_ordhd b,ORD_ORDDTL c, v_ord_org org
        where a.MTORNO=c.MTORNO  and a.MTORNO=b.MTORNO and b.mlorno=c.mlorno and a.channo=org.channo and a.ordorg=org.orgno
        and c.suitno in('T00','T01')
         and b.isfect=1
         and a.ORTYNO='DZ' and a.ORMTNO=#{ormtno} 

         	 and org.yxgsno=#{yxgsno} 


         	and a.channo=#{channo}

         group by a.CHANNO,org.yxgsno ,org.yxgsnm,c.SAMPNO
      ) c on b.sampno=c.sampno
      where 1=1

      	and lower(b.sampnm) like lower('%${sampnm}%')


      and a.bradno=#{bradno} 

      	 and a.spclno=#{spclno} 


      	 and a.sptyno=#{sptyno}


     	and a.spseno=#{spseno} 

      and c.ORMTQT!=0
      order by a.spclno,a.sptyno,a.spseno,a.spbano,spbseno

	 select b.sampnm as yangybh,a.spclno,a.sptyno,a.spseno,a.spbano,spbseno,a.sprtpr as lingsj,c.yxgsnm as yxgsmc,c.ORMTQT as shul
          from ORD_SAMPLE_PLAN a
      inner join ORD_SAMPLE_DESIGN b on A.PLSPNO=B.PLSPNO 
      inner join (
        select org.yxgsno ,org.yxgsnm,c.SAMPNO,sum(c.ORMTQT) ORMTQT
        from ORD_ORD a,ord_ordhd b,ORD_ORDDTL c, v_ord_org org
        where a.MTORNO=c.MTORNO  and a.MTORNO=b.MTORNO and b.mlorno=c.mlorno and a.channo=org.channo and a.ordorg=org.orgno
        and c.suitno in('T00','T01')
        and b.isfect=1
         and a.ORTYNO='DZ' and a.ORMTNO=#{ormtno}

         	 and org.yxgsno=#{yxgsno} 


         	and a.channo=#{channo}

         group by org.yxgsno ,org.yxgsnm,c.SAMPNO
      ) c on b.sampno=c.sampno
      where 1=1

      	and lower(b.sampnm) like lower('%${sampnm}%')


      and a.bradno=#{bradno} 

      	 and a.spclno=#{spclno} 


      	 and a.sptyno=#{sptyno}


     	and a.spseno=#{spseno} 

     and c.ORMTQT!=0
    order by a.spclno,a.sptyno,a.spseno,a.spbano,spbseno

	 select b.sampnm as yangybh,a.spclno,a.sptyno,a.spseno,a.spbano,spbseno,a.sprtpr as lingsj,c.yxgsnm as yxgsmc,c.ORMTQT as shul,d.spsuno
          from ORD_SAMPLE_PLAN a
      inner join ORD_SAMPLE_DESIGN b on A.PLSPNO=B.PLSPNO 
      inner join ord_sample_colth d on b.sampno=d.sampno 
      inner join (
        select org.yxgsno ,org.yxgsnm,c.SAMPNO,sum(c.ORMTQT) ORMTQT
        from ORD_ORD a,ord_ordhd b,ORD_ORDDTL c, v_ord_org org
        where a.MTORNO=c.MTORNO and a.MTORNO=b.MTORNO and b.mlorno=c.mlorno  and a.channo=org.channo and a.ordorg=org.orgno
        and c.suitno in('T00','T01')
         and b.isfect=1
         and a.ORTYNO='DZ' and a.ORMTNO=#{ormtno} 

         	 and org.yxgsno=#{yxgsno} 


         	and a.channo=#{channo}

         group by org.yxgsno ,org.yxgsnm,c.SAMPNO
      ) c on b.sampno=c.sampno
      where 1=1

      	and lower(b.sampnm0 like lower('%${sampnm}%')


      and a.bradno=#{bradno} 

      	 and a.spclno=#{spclno} 


      	 and a.sptyno=#{sptyno}


     	and a.spseno=#{spseno} 

      and c.ORMTQT!=0
      order by a.spclno,a.sptyno,a.spseno,a.spbano,spbseno
