mapper.select:
select c.bradno, sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt) as ormtqt, round(sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt*c.spftpr)/10000,2) ormtam, count(distinct b.sampno) sampnocount from ord_ord e,ord_orddtl a,ord_sample_design b,ord_sample_plan c where a.sampno=b.sampno and b.plspno=c.plspno and e.mtorno=a.mtorno and e.ormtno=#{ormtno} and e.ortyno='DZ'
AND e.ordorg=#{ordorg}
and exists (select 1 from v_ord_org f where E.ordorg=f.orgno and f.yxgsno=#{ordorg})
and c.bradno=#{bradno}
group by c.bradno order by sum(a.ormtqt) desc

select sum(qymtqt) qymtqt,sum(qymtam) qymtam from ord_plan_org a,ord_plan_orgdtl b where a.plorno=b.plorno and a.ormtno=#{ormtno} and a.bradno='Y'
AND a.ordorg=#{ordorg}
and exists (select 1 from v_ord_org f where a.ordorg=f.orgno and f.yxgsno=#{ordorg})

    	select sum(plmtam) plmtam,sum(plmtqt) plmtqt from ord_plan_hd a
      where a.ormtno=#{ormtno}  and a.bradno=#{bradno}


	         		AND a.ordorg=#{ordorg}


	         		and a.orgno=#{ordorg}

mapper.sql:
    	 select  b.sampno,b.sampnm,c.sprtpr,nvl(f.thumb,f.imgnm) imgnm,sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt) ormtqt 
	      from ord_ord e
	      inner join ord_orddtl a on e.mtorno=a.mtorno
	      inner join ord_sample_design b on a.sampno=b.sampno
	      inner join ord_sample_plan c on  b.plspno=c.plspno  
	      left join (
	           select sampno,imgnm,thumb from ord_sample_photo a
	           where not exists(select 1 from ord_sample_photo b where a.sampno=b.sampno and a.id>b.id)
	      ) f on b.sampno=f.sampno
	      where  
	           e.ormtno=#{ormtno} and e.ortyno='DZ' 


	         		AND e.ordorg=#{ordorg}


	         		and exists (select 1 from v_ord_org f where E.ordorg=f.orgno  and f.yxgsno=#{ordorg}) 




	        and c.bradno=#{bradno}

		  		and c.spclno=#{spclno}


		  		and c.sptyno=#{sptyno}


		  		and c.spseno=#{spseno}

	        group by b.sampno,b.sampnm,c.sprtpr,f.imgnm,thumb

mapper.select:
    	select  sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt) as ormtqt,

     		 round(sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt*c.sprtpr)/10000,2) ormtam,


     		 round(sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt*c.spftpr)/10000,2) ormtam,


        count(distinct b.sampno) sampnocount
      from ord_ord e,ord_orddtl a,ord_sample_design b,ord_sample_plan c
      where a.sampno=b.sampno and b.plspno=c.plspno  
        and e.mtorno=a.mtorno and e.ormtno=#{ormtno} and e.ortyno='DZ' 


	         		AND e.ordorg=#{ordorg}


	         		and exists (select 1 from v_ord_org f where E.ordorg=f.orgno  and f.yxgsno=#{ordorg}) 




        	and c.bradno=#{bradno}

		  		and c.spclno=#{spclno}


		  		and c.sptyno=#{sptyno}


		  		and c.spseno=#{spseno}

    	select x.*,0 qymtqt,0 qymtam from (
		  select c.spclno,d.itnm as spclnm, sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt) as ormtqt
		  ,sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt*sprtpr) ormtam
		  from ord_ord e,ord_orddtl a,ord_sample_design b,ord_sample_plan c,ord_pub_code d
		  where a.sampno=b.sampno and b.plspno=c.plspno and c.spclno=d.itno and d.tyno=0
		  and e.mtorno=a.mtorno and e.ortyno='DZ' and e.ormtno=#{ormtno}  and e.ordorg=#{ordorg}
		  and c.bradno=#{bradno} 
		  group by c.spclno,d.itnm
		) x 
		order by x.spclno

    	select x.*,0 qymtqt,0 qymtam from (
		  select c.sptyno spclno,d.itnm as spclnm, sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt) as ormtqt
		  	,sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt*sprtpr) ormtam
		  from ord_ord e,ord_orddtl a,ord_sample_design b,ord_sample_plan c,ord_pub_code d
		  where a.sampno=b.sampno and b.plspno=c.plspno and c.sptyno=d.itno and d.tyno='2'
		  and e.mtorno=a.mtorno and e.ortyno='DZ' and e.ormtno=#{ormtno} and e.ordorg=#{ordorg}
		  and c.bradno=#{bradno} and c.spclno=#{spclno}
		  group by c.sptyno,d.itnm
		) x 
		order by x.spclno

    	select x.*,0 qymtqt,0 qymtam from (
		  select c.spseno spclno,d.itnm as spclnm, sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt) as ormtqt
		  ,sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt*sprtpr) ormtam
		  from ord_ord e,ord_orddtl a,ord_sample_design b,ord_sample_plan c,ord_pub_code d
		  where a.sampno=b.sampno and b.plspno=c.plspno and c.spseno=d.itno and d.tyno='5'
		  and e.mtorno=a.mtorno and e.ortyno='DZ' and e.ormtno=#{ormtno}  and e.ordorg=#{ordorg}
		  and c.bradno=#{bradno} and c.spclno=#{spclno}
		  group by c.spseno,d.itnm
		) x 
		order by x.spclno

select c.sprtpr, sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt) as ormtqt, ratio_to_report(sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt)) over() as ormtqt_zb, sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt*c.sprtpr) ormtam, ratio_to_report(sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt*c.sprtpr)) over() as ormtam_zb, count(distinct b.sampno) sampnocount, ratio_to_report( count(distinct b.sampno)) over() as sampnocount_zb from ord_ord e,ord_orddtl a,ord_sample_design b,ord_sample_plan c where a.sampno=b.sampno and b.plspno=c.plspno and e.mtorno=a.mtorno and e.ormtno=#{ormtno} and e.ortyno='DZ'
and exists (select 1 from v_ord_org f where E.ordorg=f.orgno and f.ORGNO=#{ordorg})
and exists (select 1 from v_ord_org f where e.ordorg=f.orgno and f.QYNO=#{ordorg})
and exists (select 1 from v_ord_org f where e.ordorg=f.orgno and f.yxgsno=#{ordorg})
and c.bradno=#{bradno}
and c.spclno=#{spclno}
and c.sptyno=#{sptyno}
and c.spseno=#{spseno}
group by c.sprtpr

     	 select x.*,y.qymtqt,y.qymtam from (     
		      select f.qyno as orgno,f.qynm as orgnm, sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt) as ormtqt,sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt*spftpr) ormtam
		      from ord_ord e,ord_orddtl a,ord_sample_design b,ord_sample_plan c,v_ord_org f
		      where a.sampno=b.sampno and b.plspno=c.plspno  
		      and e.mtorno=a.mtorno and e.ortyno='DZ' and e.ormtno=#{ormtno} and c.bradno=#{bradno}
		      and e.ordorg=f.orgno and f.YXGSNO=#{ordorg}   

			  		and c.spclno=#{spclno}


			  		and c.sptyno=#{sptyno}


			  		and c.spseno=#{spseno}

		      group by f.qyno,f.qynm
		) x,(    
		      select f.qyno as orgno,sum(qymtqt) qymtqt,sum(qymtam) qymtam 
				  from ord_plan_org a,ord_plan_orgdtl b,v_ord_org f
				  where 
				  a.plorno=b.plorno and a.ormtno=#{ormtno}  and a.bradno=#{bradno}
				  and A.ordorg=f.orgno and f.YXGSNO=#{ordorg}

				  		and b.spclno=#{spclno}


				  		and b.sptyno=#{sptyno}


				  		and b.spseno=#{spseno}

				  group by f.qyno
		 ) y
		 where x.orgno=y.orgno

     	  select x.*,y.qymtqt,y.qymtam from (     
		      select f.YXGSNO as orgno,f.YXGSNM as orgnm, sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt) as ormtqt,sum(decode(a.suitno,'T01',0.75,'T02',0.25,'T04',0.25,1)*a.ormtqt*spftpr) ormtam
		      from ord_ord e,ord_orddtl a,ord_sample_design b,ord_sample_plan c,v_ord_org f
		      where a.sampno=b.sampno and b.plspno=c.plspno  
		      and e.mtorno=a.mtorno and e.ortyno='DZ' and e.ormtno=#{ormtno}
		      and e.ordorg=f.orgno 
		      and c.bradno=#{bradno}

			  		and c.spclno=#{spclno}


			  		and c.sptyno=#{sptyno}


			  		and c.spseno=#{spseno}

		      group by f.YXGSNO,f.YXGSNM
		) x,(    
		      select f.YXGSNO as orgno,sum(qymtqt) qymtqt,sum(qymtam) qymtam 
				  from ord_plan_org a,ord_plan_orgdtl b,v_ord_org f
				  where 
				  a.plorno=b.plorno and a.ormtno=#{ormtno}  and a.bradno=#{bradno}
				  and A.ordorg=f.orgno 

				  		and b.spclno=#{spclno}


				  		and b.sptyno=#{sptyno}


				  		and b.spseno=#{spseno}

				  group by f.YXGSNO
		 ) y
		 where x.orgno=y.orgno 
		 ORDER BY X.ORGNO
