mapper.sql:
select distinct a.ormtno,a.spyear,c.suitno,a.bradno,a.spsean,a.spbseno,a.sprseno,a.spclno,a.sptyno,a.spseno,a.splcno,a.spbano ,b.sampno,b.sampnm,b.versno,b.stseno,b.buspno,b.spmtno,b.gustno,b.colrno,b.pattno,b.stylno,b.stylgp,b.sexno,b.slveno ,b.suitty,b.desp ,h.prsuno , e.sizenm as sizegp_name ,g.prseqm,g.prodnm,g.prodds,e.sizeno as sizegp,g.prtype,g.prprpt,g.prorgd,DECODE(C.SUITNO,'T01','件','T03','件','T02','条','T04','条',U.SPUNMS) prunit,i.mateno,g.prdcno ,STPR.spftpr prftpr ,STPR.sprtpr prrtpr,h.spctpr as prctpr,g.prmtam,j.prmlam,g.prorpr,(h.spclbd+h.acsyam) prflam, g.prmark ,decode(f.sampno,null,'未订货','已订货') sample_state from ord_sample_plan a inner join ord_sample_design b on a.plspno=b.plspno inner join ORD_SAMPLE_DESIGN_SIZEGP c on b.sampno=c.sampno inner join ORD_SAMPLE_DESIGN_STPR STPR on b.sampno=STPR.sampno AND C.SUITNO=STPR.SUITNO inner join ord_size d on c.sizegp=d.sizeno and d.sizety='PRDSZFW' and d.ysizety='PRDSZTY' and d.ormtno=#{ormtno} inner join ORD_PUB_SIZE e on e.sizety='PRDSZTY' and e.sizety=d.ysizety and e.sizeno=d.ysizeno and e.szbrad=#{bradno} and a.spclno=e.szclno left join ord_sample_colth h on b.sampno=h.sampno left join (select * from ord_sample_mate where mateso=1) i on b.sampno=i.sampno left join (select sampno,sum(mtpupr*mtcnqt) prmlam from ord_sample_mate group by sampno) j on b.sampno=j.sampno left join (select sampno,sum(ormtqt) ormtqt from ord_orddtl where (ormtqs!=0 or ormtqt!=0) group by sampno) f on b.sampno=f.sampno LEFT JOIN ORD_SAMPLE_UNMS U ON U.SPCLNO=A.SPCLNO AND U.SPTYNO=A.SPTYNO AND U.SPSENO=A.SPSENO left join ORD_SAMPLE_PROD g on a.ormtno=g.ormtno and b.sampno=g.sampno and c.suitno=g.suitno where a.ormtno=#{ormtno} and a.bradno=#{bradno} and not exists (select 1 from ORD_COMP_PAL p where a.ormtno=p.ormtno and b.sampno=p.sampno and p.paltpy='取消')
and a.spclno=#{spclno}
and exists ( select 1 from t_role_user x,t_role_class y where x.role_id=y.role_id and a.spclno=y.itno and x.user_id=#{user_id} )
and a.sptyno=#{sptyno}
and a.spseno=#{spseno}
and h.prsuno=#{prsuno}
and g.sampno is not null
and g.sampno is null
and lower(b.sampnm) like lower('%${sampnm}%')
and lower(g.prodnm) like lower('%${prodnm}%')
and f.sampno is not null and f.ormtqt!=0
and f.sampno is null
